const Web3 = require("web3");

const {apiUrl, contractAddress, contractAbi, senderAddress, receiverAddress, tokenId} = data;

const web3 = new Web3(apiUrl); 

try {
  const nftContract = new web3.eth.Contract(contractAbi, contractAddress);
  const transactionData = nftContract.methods.safeTransferFrom( senderAddress, receiverAddress,tokenId).encodeABI();
  outputs["transaction data"](transactionData, tag);
} catch (e) {
  outputs.error({error: String(e)}, tag);
}
