const {storeId, data: value} = data;
if (value === undefined) {
  // reading
  const store = shared[storeId];
  if (store === undefined) {
    outputs.error({"error": `Store ${storeId} not found.`}, tag);
  } else {
    outputs.data(store, tag);
  }
} else {
  // writing
  shared[storeId] = value;
  outputs.written({storeId, data: value}, tag);
}
