const sqlite3 = require("sqlite3");
const {id, type, options = {}} = data;
const {sql, params} = options;
const db = shared[id];
switch (type) {
case "open":
  const {filename, mode} = options;
  if (db)
    outputs.done(null, tag);
  else
    shared[id] = new sqlite3.Database(
      filename, mode, (error) => error 
        ? outputs.error(`${error}`, tag) 
        : outputs.done(null, tag)
    );
  break;
case "run":
  db.run(sql, params, function (error) {
    if (error)
      outputs.error(`${error}`, tag);
    else {
      outputs.data(this, tag);
      outputs.done(null, tag);
    }
  });
  break;
case "get":
  db.get(sql, params, (error, row) => {
    if (error)
      outputs.error(`${error}`, tag);
    else {
      outputs.data(row || null, tag);
      outputs.done(null, tag);
    }
  });
  break;
case "all":
  db.all(sql, params, (error, rows) => {
    if (error)
      outputs.error(`${error}`, tag);
    else {
      outputs.data(rows, tag);
      outputs.done(null, tag);
    }
  });
  break;
case "each":
  db.each(sql, params, 
    (error, row) => error
      ? outputs.error(`${error}`, tag)
      : outputs.data(row, tag),
    () => outputs.done(null, tag));
  break;
case "close":
  if (db) {
    delete shared[id];
    db.close((error) => error
      ? outputs.error(`${error}`)
      : outputs.done(null, tag));
  } else
    outputs.done(null, tag);
  break;
}
