const { exec } = require( "child_process" );
const { sync } = data;
const { dependencies } = params;
if (data.start !== false ){
  const command = "cd " + data.projectPath + " && " + "npm install " + params.dependencies.join(' ');
  outputs.stdout( command, tag );
  if (state.running){
    outputs.bounced(data.sync, tag);
  } else {
    state.running = true;
    exec(command, (error, stdout, stderr) => {
      if (error) {
        outputs.error(`${error}`, tag);
      } else {
        outputs.stdout(stdout || "", tag);
        outputs.stderr(stderr || "", tag);
        outputs.error(null, tag);
      }
      state.running = false;
    });
  }
}
