const { exec } = require( "child_process" );
const { dependencies } = params;
if (data.start !== false ){
  const command = "cd " + data.projectPath + " && " + "npm install " + params.dependencies.join(' ');
  outputs.stdout( {"stdout" : command}, tag );
  if (state.running){
    outputs.bounced({"bounced" : data}, tag);
  } else {
    state.running = true;
    exec(command, (error, stdout, stderr) => {
      if (error) {
        outputs.error( {"error" : `${error}`}, tag);
      } else {
        outputs.stdout(stdout || {"stdout" : ""}, tag);
        outputs.stderr(stderr || {"stderr" : ""}, tag);
      }
      state.running = false;
    });
  }
}
