const fs = require("fs");
const { exec } = require( "child_process" );
const { replace } = params;
const command = "npx create-react-app " + data + " --use-npm";
if (state.running){
  outputs.bounced({"bounced" : data }, tag);
} else {
  state.running = true;
  if ( params.replace === true && fs.existsSync(data + "/.cranq-react-app") ) {
    outputs.stdout({"stdout" : "Removing react application at " + data});
    fs.rmSync( data, { recursive: true, force: true }, (error) => { 
      outputs.error({ "error" : "Error deleting react application."});
    });
  }
  outputs.stdout( {"stdout" : command}, tag );
  exec(command, (error, stdout, stderr) => {
if (error && (params.replace === false || 'undefined' )) {
  outputs.error({ "error" : "Application folder already exists. Set the replace input to true if you wish to reinstall the application from scratch" }, tag);
  outputs.success(false, tag);
} else {
  outputs.stdout(stdout || {"stdout" : ""}, tag);
  outputs.stderr(stderr || {"stderr" : ""}, tag);
  fs.writeFileSync(data + "/.cranq-react-app", "");
  outputs.success(true, tag);
}
  state.running = false;
  });
}
