try {
  const {google} = require('googleapis');
  const {sessionid, type, options = {}} = data;
  let jwtClient = shared[sessionid];

  if (type === "service_account_auth") {
    const {email, keyFile = null, key} = options;
    jwtClient = shared[sessionid] = new google.auth.JWT(
         email,
         keyFile,
         key,
         ['https://www.googleapis.com/auth/spreadsheets']);

    outputs.done(null, tag);
  }

  const sheets = google.sheets({version: 'v4', auth: jwtClient});

  switch (type) {
    case "update":
      const {spreadsheetId, updatemeta, values} = options;
      const {range, valueInputOption = "RAW"} = updatemeta;

      sheets.spreadsheets.values.update({
        spreadsheetId: spreadsheetId,
        range: range,
        valueInputOption: valueInputOption,
        resource: {
          values: values
        },
      },function (err, response) {
        if (err){
          outputs.error({error: String(err)}, tag);
        } else {
          outputs.done(null, tag);
        }
      })
    break;
  }


}
catch (err) {
  outputs.error({error: String(err)}, tag);
}

