const Web3 = require('web3');
const {transaction_hash, provider} = data; 


var web3 = new Web3(provider);

web3.eth.getTransactionReceipt(transaction_hash)
  .then((tr)=> {
    outputs.transaction_receipt(tr, tag);
  })
  .catch((error)=>{
    outputs.error({error: error.toString()}, tag)
})

