const {
  TranscribeStreamingClient,
  StartStreamTranscriptionCommand,
} = require("@aws-sdk/client-transcribe-streaming");
const {
  createReadStream
} = require("fs");
const { PassThrough } = require('stream');
const wav = require('wav');

const {
  fileName,
    params: {
      region,
      language = "en-US",
      accessKeyId,
      secretAccessKey,
    }
} = data;

async function startRequest() {
    const reader = new wav.Reader();

    const audioSource = createReadStream(fileName, {highWaterMark: 8 * 1024});
    audioSource.pipe(reader);

    reader.on('format', async function (format) {
        const MediaSampleRateHertz = format.sampleRate;
        const LanguageCode = language;
        const MediaEncoding = 'pcm';
        const audioPayloadStream = new PassThrough({});  //Fixme
        reader.pipe(audioPayloadStream);

        const client = new TranscribeStreamingClient({
            region,
            credentials: {
                accessKeyId,
                secretAccessKey,
            },
        });

        const params = {
            LanguageCode,
            MediaEncoding,
            MediaSampleRateHertz,
            AudioStream: (async function* () {
                for await (const chunk of audioPayloadStream) {
                    yield {
                        AudioEvent: {
                            AudioChunk: chunk,
                        },
                    };
                }
            })(),
        };
        const command = new StartStreamTranscriptionCommand(params);
        const response = await client.send(command);
        let message = {};

        try {
            for await (const event of response.TranscriptResultStream) {
                if (event.TranscriptEvent?.Transcript?.Results?.length > 0) {
                    outputs['current'](JSON.stringify(event))
                    message = event;
                }
            }
            outputs['result'](JSON.stringify(message))
        } catch (err) {
            outputs['error']({
              error: err
            })
        }
    });
}
startRequest();
