const {
    PollyClient,
    SynthesizeSpeechCommand
} = require("@aws-sdk/client-polly");
const {
    writeFile
} = require("fs").promises;

const synthesis = async () => {
    try {
         const {
        ["MP3 file out path"]: fileName,
        text,
        params: {
            region,
            language = "",
            accessKeyId,
            secretAccessKey,
            voiceId = "Amy",
            engine,
            sampleRate
        }
    } = data

        const polly = new PollyClient({
            region,
            credentials: {
                accessKeyId,
                secretAccessKey,
            },
        });

        const params = {
            OutputFormat: "mp3",
            Text: text,
            VoiceId: voiceId,
            ...(language ? {
                LanguageCode: language
            } : {}),
            ...(engine ? {
                Engine: engine
            } : {}),
            ...(sampleRate ? {
                SampleRate: sampleRate
            } : {})
        };

        const synthesizeSpeechCommand = new SynthesizeSpeechCommand(params);

        console.log(outputs)

        const audioData = await polly.send(synthesizeSpeechCommand);
        const audioStream = audioData.AudioStream;
        await writeFile(fileName, audioStream);
        outputs['result'](fileName, tag);
    } catch (err) {
        outputs['error']({error: err}, tag);
    }
};

synthesis();
