const fs = require("fs");
const sdk = require("microsoft-cognitiveservices-speech-sdk");
const {
    fileName,
    params: {
        subscriptionKey,
        region,
        language = "en-US"
    }
} = data;

const speechConfig = sdk.SpeechConfig.fromSubscription(subscriptionKey, region);
speechConfig.speechRecognitionLanguage = language;

let audioConfig = sdk.AudioConfig.fromWavFileInput(fs.readFileSync(fileName));
let speechRecognizer = new sdk.SpeechRecognizer(speechConfig, audioConfig);
let parts = [];

speechRecognizer.recognized = (s, e) => {
    if (e.result.reason == sdk.ResultReason.RecognizedSpeech) {
        outputs['partial result'](e.result.text, tag);
        parts.push(e.result.text);
    }
};
speechRecognizer.canceled = (s, e) => {
    if (e.reason == sdk.CancellationReason.Error) {
        outputs['error'](`ErrorCode=${e.errorCode}, ErrorDetails=${e.errorDetails}`);
    }
    else {
        outputs['result'](parts.join(" "), tag);
    }
    speechRecognizer.stopContinuousRecognitionAsync();
    speechRecognizer.close();
};
speechRecognizer.sessionStopped = (s, e) => {
    speechRecognizer.stopContinuousRecognitionAsync();
};

speechRecognizer.startContinuousRecognitionAsync();
