const fs = require("fs");
const sdk = require("microsoft-cognitiveservices-speech-sdk");
const {
  fileName,
  params: {
    subscriptionKey,
    region,
    language = "en-US"
  }
} = data;

const speechConfig = sdk.SpeechConfig.fromSubscription(
  subscriptionKey, region);
speechConfig.speechRecognitionLanguage = language;

const fileContents = fs.readFileSync(fileName);
const audioConfig = sdk.AudioConfig.fromWavFileInput(
  fileContents);
const speechRecognizer = new sdk.SpeechRecognizer(
  speechConfig, audioConfig);

speechRecognizer.recognizeOnceAsync(result => {
  const {duration, errorDetails, language, languageDetectionConfidence, offset, properties, reason, resultId, speakerId, text} = result;
  const resultPlain = {duration, errorDetails, language, languageDetectionConfidence, offset, properties: JSON.parse(JSON.stringify(properties)), reason, resultId, speakerId, text};
  switch (result.reason) {
    case sdk.ResultReason.RecognizedSpeech:
      outputs['result'](resultPlain, tag);
      break;
    case sdk.ResultReason.NoMatch:
      outputs['error']({
        error: "Speech could not be recognized."
      }, tag);
      break;
    case sdk.ResultReason.Canceled:
      const cancellation = sdk.CancellationDetails.fromResult(result);
      const errorText = (cancellation.reason === sdk.CancellationReason.Error)
      ? `, ErrorCode=${cancellation.ErrorCode}, ErrorDetails=${cancellation.errorDetails}`
      : "";
      outputs['error'](
        {error: `CANCELED: Reason=${cancellation.reason} ${errorText}`}, tag);
      break;
  }
  speechRecognizer.close();
}, (err) => outputs.error({error: String(err)}, tag));
