// TODO: consider replacing this library
const { verify, PlatformAlgorithm } = require("discord-verify/node");
const crypto = require('node:crypto')

const {
  request,
  publicKey
} = data;

verify(
  JSON.stringify(request.body),
  request.headers["x-signature-ed25519"],
  request.headers["x-signature-timestamp"],
  publicKey,
  crypto.webcrypto.subtle,
  PlatformAlgorithm.OldNode).then(valid => {
  if (valid) {
    outputs.valid(request, tag);
  } else {
    outputs["not valid"](request, tag);
  }
})
