const jwt = require('jsonwebtoken');
const {id, options = {}} = data;

try {
  switch (id) {
    case 'sign':
      sign(options);
      break;
    case 'verify':
      verify(options)
      break;
    default:
      outputs.error({"error": "Unknown id!"}, tag);
  }
} catch (e){
  outputs.error({"error": `Error happend during ${id}`,  details: e}, tag);
}

function sign(options) {
  const {payload, secretOrPrivateKey, options: verifyOptions} = options;
  jwt.sign(payload, secretOrPrivateKey, verifyOptions, function(err, token) {
    if (err) {
      outputs.error({"error": "Error happend during sign", "details": err}, tag);
    } else {
      outputs.result({token}, tag);
    }
  });
}


function verify(options){
  const {token, secretOrPrivateKey, options: signOptions} = options;
  jwt.verify(token, secretOrPrivateKey, signOptions, function(err, token) {
    if (err) {
      outputs.error({"error": "Error happend during verify", "details": err}, tag);
    } else {
      outputs.result({token}, tag);
    }
  });
}

