const { Configuration, OpenAIApi } = require("openai");
const {request, params: {apiKey}} = data;
const configuration = new Configuration({apiKey});
const openai = new OpenAIApi(configuration);

async function main() {
  try {
    const response = await openai.createCompletion(request);
    outputs['response'](response.data, tag);
  } catch (error) {
    if (error.response) {
      const message = `[${error.response.status}] ${JSON.stringify(error.response.data)}`;
      outputs['error']({error: message}, tag);
    } else {
      outputs['error']({error: error.message}, tag);
    }
  }
}

main();
