const {exec} = require("child_process");
const {command, options} = data;
if (state.running)
  outputs.bounced(data, tag);
else {
  state.running = true;
  exec(command, options, (error, stdout, stderr) => {
    if (error)
      outputs.error(`${error}`, tag);
    else {
      outputs.stdout(stdout || "", tag);
      outputs.stderr(stderr || "", tag);
      outputs.error(null, tag);
    }
    state.running = false;
  });
}
